import{_ as d}from"./CC-BY-NC-SA-BWo1x6F4.js";import{c as i,d as e,o as v,_ as s}from"./index-Xjrq8Jsx.js";const t={class:"courses ui container"};function l(f,a,m,n,u,h){return v(),i("div",t,a[0]||(a[0]=[e('<h1 class="ui header" data-v-f9925e47>自主學習準備課程-教師篇<div class="sub header" data-v-f9925e47>深入瞭解自主學習的重要性，並為自己設定學習目標</div></h1><img class="card-img" src="'+d+'" data-v-f9925e47><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>課程基本資訊</h2><div class="ui list" data-v-f9925e47><div class="item" data-v-f9925e47><i class="users icon" data-v-f9925e47></i><div class="content" data-v-f9925e47>人數：7~24人</div></div><div class="item" data-v-f9925e47><i class="clock icon" data-v-f9925e47></i><div class="content" data-v-f9925e47>時間：三小時一堂</div></div><div class="item" data-v-f9925e47><i class="calendar icon" data-v-f9925e47></i><div class="content" data-v-f9925e47>期程：16堂課一期</div></div><div class="item" data-v-f9925e47><i class="sync alternate icon" data-v-f9925e47></i><div class="content" data-v-f9925e47>課程進行方式：互動討論式、分組進行(大組、小組)</div></div></div></div><div class="ui segments" data-v-f9925e47><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>一、自我介紹(白紙操作)</h2><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>畫一張你和學生的圖像</div><div class="item" data-v-f9925e47>你認為教師這個角色的定位與功能為何？</div><div class="item" data-v-f9925e47>為什麼你要當老師呢？</div><div class="item" data-v-f9925e47>你對這個職業角色的感覺如何？曾經感到厭倦或困難嗎？</div><div class="item" data-v-f9925e47>分享交流</div><div class="item" data-v-f9925e47>作用：自我鏡照，並作為後續建立學習檔案之資料</div></div></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>二、建立討論文化(一)</h2><h3 class="ui header" data-v-f9925e47>組內討論角色分配與功能</h3><div class="ui list" data-v-f9925e47><div class="item" data-v-f9925e47><i class="user icon" data-v-f9925e47></i><div class="content" data-v-f9925e47>主持人</div></div><div class="item" data-v-f9925e47><i class="pencil alternate icon" data-v-f9925e47></i><div class="content" data-v-f9925e47>記錄</div></div><div class="item" data-v-f9925e47><i class="hourglass half icon" data-v-f9925e47></i><div class="content" data-v-f9925e47>時間精靈</div></div><div class="item" data-v-f9925e47><i class="heart icon" data-v-f9925e47></i><div class="content" data-v-f9925e47>心靈天使</div></div><div class="item" data-v-f9925e47><i class="bullhorn icon" data-v-f9925e47></i><div class="content" data-v-f9925e47>報告人</div></div><div class="item" data-v-f9925e47><i class="users icon" data-v-f9925e47></i><div class="content" data-v-f9925e47>參與者</div></div></div><p data-v-f9925e47>建立課堂共識(實作)</p></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>三、建立討論文化(二)</h2><p data-v-f9925e47>了解討論文化的七項要素(講解)</p><div class="ui ordered list" data-v-f9925e47><div class="item" data-v-f9925e47>聆聽</div><div class="item" data-v-f9925e47>尊重感受</div><div class="item" data-v-f9925e47>合作的態度(協同思考而非辯論)</div><div class="item" data-v-f9925e47>提意見要附理由</div><div class="item" data-v-f9925e47>用詞定義釐清</div><div class="item" data-v-f9925e47>界線清楚</div><div class="item" data-v-f9925e47>結論開放</div></div><p data-v-f9925e47>親師生的界限概念與釐清操作</p></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>四、課堂動力系統(一)</h2><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>師生對應模式</div><div class="item" data-v-f9925e47>傳統教育課堂、開放教育課堂與自主學習課堂的異同</div><div class="item indent" data-v-f9925e47>教師主體 / 學生中心 / 師生互為主體</div></div></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>五、課堂動力系統(二)</h2><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>講述型課堂 (齊一進度 / 個別觀照 / 動態搭配)</div><div class="item" data-v-f9925e47>討論型課堂 (分工負責 / 發散討論 / 協作參與)</div><div class="item" data-v-f9925e47>實作型課堂 (單向示範仿作 / 自由創作 / 協作共學)</div><div class="item" data-v-f9925e47>考試評量 (紙筆測驗 / 主觀評量 / 多元評量)</div></div></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>六、課堂動力系統(三)</h2><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>導師班的動力系統</div><div class="item" data-v-f9925e47>瞭解學習風格與多元智能</div><div class="item" data-v-f9925e47>瞭解個別差異與善用學生優點</div><div class="item" data-v-f9925e47>建立學生社群內的正向互動系統</div></div></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>七、課堂動力系統(四)</h2><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>學科班的動力系統</div><div class="item" data-v-f9925e47>20 - 60 - 20現象與因應</div><div class="item" data-v-f9925e47>建立互助學習的機制</div><div class="item" data-v-f9925e47>實作</div></div></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>八、課堂動力系統(五)</h2><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>實作案例討論</div><div class="item" data-v-f9925e47>現場課堂實作與檢視</div></div></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>九、課堂動力系統(六)</h2><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>實作案例討論</div><div class="item" data-v-f9925e47>現場課堂實作與檢視</div></div></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>十、教師作為一個學習者(一)</h2><h3 class="ui header" data-v-f9925e47>學習的基本能力與配備</h3><p data-v-f9925e47>自主學習的四個階段</p><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>解放 =&gt; 安全感</div><div class="item" data-v-f9925e47>自我中心 =&gt; 界限</div><div class="item" data-v-f9925e47>眼高手低 =&gt; 小階梯與成功經驗</div><div class="item" data-v-f9925e47>自主成熟 =&gt; 知、行、悟的螺旋向上</div></div><p data-v-f9925e47>正向的學習循環</p><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>加分概念</div><div class="item" data-v-f9925e47>PDCA循環<div class="ui ordered list" data-v-f9925e47><div class="item" data-v-f9925e47>Plan =&gt; 計畫</div><div class="item" data-v-f9925e47>Do =&gt; 執行</div><div class="item" data-v-f9925e47>Check =&gt; 檢查</div><div class="item" data-v-f9925e47>Action(增, 減, 停) =&gt; 行動(增加、減少、停止)</div></div></div><div class="item" data-v-f9925e47>習慣的力量(覺察、評估、改變、自我酬賞)</div></div></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>十一、教師作為一個學習者(二)</h2><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>學習動力初探(成長曲線圖)</div><div class="item" data-v-f9925e47>麻痺經驗與重建</div><div class="item" data-v-f9925e47>學習階梯的概念與實務(如何把大挑戰柝解成小步驟)</div><div class="item" data-v-f9925e47>以自由數學的學習地圖為例</div></div></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>十二、教師作為一個學習者(三)</h2><div class="ui bulleted list" data-v-f9925e47><div class="item" data-v-f9925e47>典範作用、教師角色與自我定位</div><div class="item" data-v-f9925e47>個人書寫</div><div class="item" data-v-f9925e47>小組分享與討論</div></div></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>十三~十五、同理能力與社群建立</h2><p data-v-f9925e47>以同理回饋即興劇場進行</p><p data-v-f9925e47>參考<a href="https://docs.google.com/document/d/17lp1gGyKwxt8gU64gMlOwPE-_p5lQeWaZ-8g8T7KsDU/edit?tab=t.0#heading=h.32ourccsnq6m" target="_blank" rel="noopener norefferer" data-v-f9925e47>Playback精華</a></p></div><div class="ui segment" data-v-f9925e47><h2 class="ui header" data-v-f9925e47>十六、回顧與展望</h2><p data-v-f9925e47>印地安羽毛形式的交流分享</p></div></div>',4)]))}const c={name:"Doc_Educators_View",data(){return{}}},g=s(c,[["render",l],["__scopeId","data-v-f9925e47"]]);export{g as default};
